    dorado.MessageBox.alert("只能选择一条记录！");
}else if (selection.length == 1) {
	var entityClass = view.get('#dataSetSysCacheConfig').get("userData")[0].entityClass;
	var springServiceName = view.get("#dataSetSysCacheConfig").getData("#.objName");
	var arr = new Array();
	arr[0] = springServiceName;
	var refressAction = view.get("#refressCacheAjaxAction");
	refressAction.set("parameter",arr);
	refressAction.execute();
