var entitys = view.get("#dataSetSysCacheConfig").getData();
if(!entitys) {
	dorado.MessageBox.alert("没有缓存需要刷新!");
	return; 
}

var arr = new Array();
entitys.each(function(item){
	var objName = item.get("objName");
	arr.push(objName);
});
var refressAllAction = view.get("#refressCacheAjaxAction");
refressAllAction.set("parameter",arr);
refressAllAction.execute();

