CustomDropDown.ClientEvent:
var currBeanPath = arg.selectedValue;
if(!currBeanPath) {
	return;
}

//获取action对象
var getIntfInfoAjaxAction = view.get("#getIntfInfoAjaxAction");

getIntfInfoAjaxAction.set("parameter",{beanPath:currBeanPath});

getIntfInfoAjaxAction.execute(function(result){
	if(result){
		var beanId = result.beanId;
		var tableId = result.tableId;
		var tableName = result.tableName;
		var objId = result.objId;
	    view.get("#dataSetSysCacheConfig").getData("#").set({"objName":beanId,"objId":objId,"tableId":tableId,"tableName":tableName});
	}else{
		view.get("#dataSetSysCacheConfig").getData("#").set({"objName":"","objId":"","tableId":"","tableName":""});
		var msg = "这个类不存在或者配置存在问题，请检查bean的配置:1,是否配置了@component（value='XXX')2,是否继承了满足规范的接口3,服务类及其接口的命名是否满足规范.详细配置方法请参考《09_IPDP通用组件使用及二次开发手册.doc》1.5.公共Webservice开发章节"
		dorado.MessageBox.alert(msg);
	}
});
