ToolBarButton.ClientEvent:
var currentNode = view.get("#categoryTree").get("currentNode");
var categoryCode="";
var parentid="";
var catetreeSeq="";

if (!currentNode) {
	//throw new dorado.Exception("请先点中一个树节点！");
	//这里主要处理的是 没有数据的情况！
    view.get("#CategoryCodeAjaxAction").set("parameter","CATEGORY_CODE").execute(function(result){
	    categoryCode=result;
    });
	
	catetreeSeq="."+categoryCode+".";
	parentid="0";
	
	view.get("#dataSetIeCategory").insert({
		    ieCategoryCode:categoryCode,
		    treeSeq:catetreeSeq,
		    ieSeq:0,
		    status:1,
		    parentIeCategoryId:parentid
	    });
	view.get("#addDialogIeCategory").show();
	
}else {

    view.get("#CategoryCodeAjaxAction").set("parameter","CATEGORY_CODE").execute(function(result){
	    categoryCode=result;
    });

    //TreeSeq
    var currEntity = currentNode.get("data");
    var seq=currEntity.get("treeSeq");
	var len=seq.substr(0,seq.length-1).lastIndexOf(".");
    var temptreeSeq=currEntity.get("treeSeq").substr(0,len+1);
	
    catetreeSeq=temptreeSeq+categoryCode+".";
	parentid=currEntity.get("parentIeCategoryId");
	
	//insert data
    var currentEntity=currentNode.get("data");

    currentNode.expandAsync(function() {
	
	    var brother = currentEntity.createBrother({
		    ieCategoryCode:categoryCode,
		    treeSeq:catetreeSeq,
		    ieSeq:0,
		    status:1,
		    parentIeCategoryId:parentid
	    });
	    view.get("#categoryTree").set("currentEntity",brother);
    });
    view.get("#addDialogIeCategory").show();
}

var currentNode = view.get("#categoryTree").get("currentNode");
var categoryCode="";
var catetreeSeq="";
var parentid="";

if (!currentNode) {
	throw new dorado.Exception("请先点中一个树节点！");
}else{

    view.get("#CategoryCodeAjaxAction").set("parameter","CATEGORY_CODE").execute(function(result){
	    categoryCode=result;
    });

    // treeSeq
    var currEntity = currentNode.get("data");
    var temptreeSeq=currEntity.get("treeSeq");
	
    catetreeSeq=temptreeSeq+categoryCode+".";
    parentid=currEntity.get("id");
}

//insertChildren data
var currentEntity=currentNode.get("data");

currentNode.expandAsync(function() {
	
	var child = currentEntity.createChild("children", {
		ieCategoryCode:categoryCode,
		treeSeq:catetreeSeq,
		ieSeq:0,
		status:1,
		parentIeCategoryId:parentid
	});
	view.get("#categoryTree").set("currentEntity",child);
});

view.get("#addDialogIeCategory").show();

var currentNode = view.get("#categoryTree").get("currentNode");
if (!currentNode) {
	throw new dorado.Exception("请先点中一个节点！");
}else{
	view.get("#dialogIeCategory").show();
}

Button.ClientEvent:
var currentNode = view.get("#categoryTree").get("currentNode");
if (!currentNode) {
	throw new dorado.Exception("请先点中一个树节点！");
}

var currentEntity = currentNode.get("data");
var categoryId=currentEntity.get("id");
var categoryName=currentEntity.get("ieCategoryName");
var categoryStatus=currentEntity.get("status");
var categoryTreeSeq=currentEntity.get("treeSeq");

if(categoryStatus!=1){
	dorado.MessageBox.alert("已经停用！");
}else{
   var res="";
      view.get("#ChickIndexAjaxAction").set("parameter",{id:categoryId,status:1,treeSeq:categoryTreeSeq}).execute(function(result){
	  res=result;
   });
   
 if(res>0){
   	   dorado.MessageBox.alert("【"+categoryName+"】或其子孙项，存在【"+res+"】个启用中的指标，请将它们停用！");
 }else if(res==0){
 	   
       currentEntity.set("status", 0); 
	   view.get("#saveIeCategorysAction").execute();
   	   dorado.MessageBox.alert("操作成功！");
	   
 }else {
       dorado.MessageBox.alert("验证时出现问题！");
 }
}

ToolBarButton.ClientEvent:
var currentNode = view.get("#categoryTree").get("currentNode");

if (!currentNode) {
	throw new dorado.Exception("请先点中一个指标分类！");
}

var currEntity = currentNode.get("data");
var categoryId = currEntity.get("id");

view.get("#dataSetIeIndex").set("readOnly",false);
view.get("#dataSetIeIndex.data").insert({ieCategoryId:categoryId,status:1});
view.get("#addDialogIeIndex").show();

var entitys = view.get("#dataSetIeIndex").getData();
var selection = view.get("#dataGridIeIndex").get("selection");
if(selection.length==0){
    dorado.MessageBox.alert("请选择记录！");
}else if(selection.length>1){
    dorado.MessageBox.alert("只能选择一条记录查看修改！");
}else if (selection.length == 1) {
    view.get("#dataSetIeIndex").getData().setCurrent(selection[0]);
    view.get("#dialogIeIndex").show();
    view.get("#dataSetIeIndex").set("readOnly",false);
}

var entitys = view.get("#dataSetIeIndex").getData();
                    var selection = view.get("#dataGridIeIndex").get("selection");
                    var action = view.get("#saveIeIndexsAction");
                    if(selection.length==0){
                        dorado.MessageBox.alert("请选择记录！");
                    }else if (selection.length != 0) {
                        dorado.MessageBox.confirm("确定删除？", function(){
                        jQuery.each(selection,function(i,item){
                            entitys.remove(item);
                        });
                        action.execute();
                        });
                    }

var entitys = view.get("#dataSetIeIndex").getData();
var found = false;
var selection = view.get("#dataGridIeIndex").get("selection");
if(selection.length==0){
    dorado.MessageBox.alert("请选择记录！");
}else if(selection.length>1){
    dorado.MessageBox.alert("只能查看一条记录！");
}else if (selection.length == 1) {
view.get("#dataSetIeIndex").getData().setCurrent(selection[0]);
view.get("#lookdialogIeIndex").show();
view.get("#dataSetIeIndex").set("readOnly",true);
}

