var cateId = currentNode.get("data").get("id");
var cateTreeSeq = currentNode.get("data").get("treeSeq");

var entity = view.get("#querydataSetIeIndex").getData("#");

entity.set("ieCategoryTreeSeq", cateTreeSeq); 

 view.get("#dataSetIeIndex").set("parameter", entity).flushAsync();

