View.ClientEvent:
window.$getIeExpRuleIeExp = function(){

	var data = view.get("#dataSetIeDeriveIeExp").getData("#");
	if (data) {
		return data.get("ieExp");
	}
}

window.$getIeExpRuleIeExpDesc = function(){

	var data = view.get("#dataSetIeDeriveIeExp").getData("#");
	if (data) {
		return data.get("ieExpDesc");
	}
}

window.$getIeExpRuleIeCode = function(){

	var data = view.get("#dataSetIeDeriveIeExp").getData("#");
	if (data) {
		return data.get("ieCode");
	}
}

window.$setIeExpRuleIeExpAndColesDialog = function(  msg ,dimenType,ieExpDesc ){
    
	var entityList  = view.get("#dataSetIeDeriveIeExp").getData();
    var data = view.get("#dataSetIeDeriveIeExp").getData("#");
    var dialog = view.get("#dialogIeDeriveIeExp");
    data.set("isUsed","0");
	data.set("islast","1");
	data.set("ieExp",msg);
	data.set("dimenType",dimenType);
	data.set("ieExpDesc",ieExpDesc);
	//将其他为最新的指标设置为非最新的
	$setIeExpRuleIsLast(entityList,data.entityId);
	
    dialog.hide();
	
}

window.$colesIeExpDialog = function(){
	
    view.get("#dataSetIeDeriveIeExp").getData("#").cancel();
    view.get("#dialogIeDeriveIeExp").hide();

}


window.$setIeExpRuleIsLast= function(entityList ,baseIeId){
	
   if(entityList.entityCount>0){
	   entityList.each(function(entity){
		  if(baseIeId!=entity.entityId){
		  	  if("1"==entity.get("islast")){
			     entity.set("islast","0");
				 $setIeExpRuleExtMsg(entity);
			  }
		  }else{
			  entity.set("islast","1");
		  }
		
	   });
   }
}

window.$setIeExpRuleExtMsg = function(entity){
	
	 if(!entity.get("dimenType")){
		  var dimenType ="" ;
          view.get("#getDimenTypeByExpIdAjaxAction").set("parameter",entity.get("id")).execute(function(result){
	            dimenType=result;
          });
		
		entity.set("dimenType",dimenType);	
     } 
	 
}

