Button.ClientEvent:
var dialog=view.get("#dialogIeIndex");
var action = view.get("#saveIeIndexsAction");
var baseAction = view.get("#saveIeDeriveIeExpsAction");

if(!action.get("hasUpdateData") && !baseAction.get("hasUpdateData")){
	dialog.hide();
	return ;
}

var baseDataSet  = view.get("#dataSetIeDeriveIeExp");

if(baseDataSet.getData().entityCount>0){
	var beforIndexData  = view.get("#dataSetIeIndex").getData("#");
    var flag = beforIndexData.isDirty();
	if(flag){  //主表发生该表的时候
	
		action.execute(function(){
		
		    var indexData  = view.get("#dataSetIeIndex").getData("#");
		
		    var ieId = indexData.get("id");
		    var ieCode = indexData.get("ieCode");
		
		    var baseDataSet  = view.get("#dataSetIeDeriveIeExp");
		    var entityList = baseDataSet.getData()
		
		    entityList.each(function(entity){
			    if(!entity.get("ieId")){
					entity.set("ieId",ieId);
					
				}
				if(!entity.get("ieCode")){
					entity.set("ieCode",ieCode);
				}
				
			});
			
		    baseAction.execute(function(){
				
			    dialog.hide();
			    baseAction.set("successMessage","指标规则保存成功");
		    });
			if(!baseAction.get("hasUpdateData")){
				dialog.hide();
			}
		
		   action.set("successMessage","基础信息保存成功");
	   });
	}else{
		var ieId = beforIndexData.get("id");
		var ieCode = beforIndexData.get("ieCode");
		
		var baseDataSet  = view.get("#dataSetIeDeriveIeExp");
		var entityList = baseDataSet.getData()
		
		entityList.each(function(entity){
			    if(!entity.get("ieId")){
					entity.set("ieId",ieId);
				}
				if(!entity.get("ieCode")){
					entity.set("ieCode",ieCode);
				}
			});
		
		baseAction.execute(function(){
			dialog.hide();
			baseAction.set("successMessage","指标规则保存成功");
		});
	}
	
}else{
		action.execute(function(){ 
			action.set("successMessage","基础信息保存成功，请继续维护规则信息"); 
		});
		
		if(baseAction.get("hasUpdateData")){
	            //这里包含一种情况就是 删除了所有规则
	            baseAction.execute(function(){
			    baseAction.set("successMessage","指标规则保存成功");
			});
         }
		 dorado.MessageBox.alert("请添加规则信息！");
		 
}

