var selection = view.get("#updateDataGridIeDeriveIeExp").get("selection");

if(selection.length==0){
	
   dorado.MessageBox.alert("请选择记录！");
   
}else if(selection.length>1){
	
   dorado.MessageBox.alert("只能选择一条记录！");
   
}else if (selection.length == 1) {
	
   view.get("#dataSetIeDeriveIeExp").getData().setCurrent(selection[0]);
   
   var baseData = view.get("#dataSetIeDeriveIeExp").getData("#")

   var isUsed = baseData.get("isUsed");
   
   if(isUsed&&"1"==isUsed){
   	
   	  dorado.MessageBox.alert("指标规则被使用过不能修改！");
	  
   }else{
       var expId =  baseData.get("id");
   
       var dimenType ="" ;
            view.get("#getDimenTypeByExpIdAjaxAction").set("parameter",expId).execute(function(result){
	            dimenType=result;
            });
		
       baseData.set("dimenType",dimenType);
		
	   view.get("#ieExpIFrame").set("path","${request.contextPath}/com.innofi.component.indicatorengine.derivedindcators.deriveexp.view.GeiZeSelect.d?status=1").refresh();
		
       view.get("#dataSetIeDeriveIeExp").set("readOnly",false);
   
       view.get("#dialogIeDeriveIeExp").show();
   }
}

var entitys = view.get("#dataSetIeDeriveIeExp").getData();
  
var selection = view.get("#updateDataGridIeDeriveIeExp").get("selection");

if(selection.length==0){
	
   dorado.MessageBox.alert("请选择记录！");
   
}else if (selection.length != 0) {
	 
	 dorado.MessageBox.confirm("确定删除？", function(){
		    var isUsedMsg ="";
			jQuery.each(selection,function(i,item){
				var isUsed = item.get("isUsed");
				if(isUsed&&"1"==isUsed){
					  isUsedMsg="指标规则被使用过不能被删除！";
				}else{
			          entitys.remove(item);
			    }
			
		   });
		   
		   if(""!=isUsedMsg){
		   	   dorado.MessageBox.alert("指标规则被使用过不能被删除！");
	       }
	});
	
	
}

var selection = view.get("#updateDataGridIeDeriveIeExp").get("selection");

if(selection.length==0){
	dorado.MessageBox.alert("请选择记录！");
}else if (selection.length >1) {
	dorado.MessageBox.alert("只能选择一条记录！");
	
}else{
    
	 view.get("#dataSetIeDeriveIeExp").getData().setCurrent(selection[0]);
     var baseData = view.get("#dataSetIeDeriveIeExp").getData("#");
	 baseData.set("islast","1");
	 //获取扩展字段的维度信息 存在就不在获取
	 $setIeExpRuleExtMsg(baseData);
	
	 //将其他的为最新的 取消
     var entityList  = view.get("#dataSetIeDeriveIeExp").getData();
     $setIeExpRuleIsLast(entityList,baseData.entityId);
	 
	 dorado.MessageBox.alert("操作成功！");
}

