DataSet.ClientEvent:
var lookStyle="${request.getParameter('lookStyle')}";
var mdId="${request.getParameter('mdId')}";
var tableId="${request.getParameter('tableId')}";
if(lookStyle =="look"){
	parm={tableId:mdId,status:"1"};
	self.set("parameter", parm).flushAsync();
	view.get("#queryFieldSet").set("visible",false);
	view.get("#queryFieldSet").set("hideMode","display");
}else{
	parm={tableId:tableId,status:"1"};
	self.set("parameter", parm).flushAsync();
}

ToolBarButton.ClientEvent:
var dataSet = view.get("#dataSetMdField");
var dialog = view.get("#updateMdFieldDialog");
var entitys = dataSet.getData();
var selection = view.get("#dataGridMdField").get("selection");

if(selection.length==0){
	dorado.MessageBox.alert("请选择记录！");
}else if(selection.length>1){
	dorado.MessageBox.alert("只能选择一条记录查看修改！");
}else if (selection.length == 1){
	dataSet.getData().setCurrent(selection[0]);
	dialog.show();
	dataSet.set("readOnly",false);
}

var dataSet = view.get("#dataSetMdField");
var dialog = view.get("#lookMdFieldDialog");
var selection = view.get("#dataGridMdField").get("selection");
	if(selection.length==0){
		dorado.MessageBox.alert("请选择记录！");
	}else if(selection.length>1){
		dorado.MessageBox.alert("只能查看一条记录！");
	}else if (selection.length == 1) {
		dataSet.getData().setCurrent(selection[0]);
		dialog.show();
		dataSet.set("readOnly",true);
	}

var fieldAction = view.get("#saveMdFieldsAction");
var dataSet = view.get("#dataSetMdField");
var fieldEntityList = dataSet.getData();
var num = 0;
	fieldEntityList.each(function(fieldEntity){

		if(fieldEntity.state == 2){
			num = num + 1;
		}
	});
if(num > 0){
	dorado.MessageBox.confirm("有"+num+"条未保存的表字段数据,是否保存？", {
		detailCallback: function(button, text) {
			if(button == "yes"){
				fieldAction.execute();
				parent.$id("updateFieldDialog").objects[0].hide();
			}
			if(button == "no"){
				parent.$id("updateFieldDialog").objects[0].hide();
			}
		}
	});
}else{
	parent.$id("updateFieldDialog").objects[0].hide();
}

var dataSet = view.get("#dataSetMdField");
var entitys = dataSet.getData();
var selection = view.get("#dataGridMdField").get("selection");
var action = view.get("#saveMdFieldsAction");

if(selection.length==0){
	dorado.MessageBox.alert("请选择记录！");
}else if (selection.length != 0) {
	dorado.MessageBox.confirm("是否删除？", function(){
			jQuery.each(selection,function(i,item){
			entitys.remove(item);
		});
		action.execute();
	});
}

var dataSet = view.get("#dataSetMdField");
var dialog = view.get("#addMdFieldDialog");
	dataSet.set("readOnly",false);
	view.get("#dataSetMdField.data").insert();
	dialog.show();

