MyDefaultCellRenderer1 = $extend(dorado.widget.grid.DefaultCellRenderer, {
    render: function(dom, arg) {
        var text = this.getText(arg.data, arg.column);
        dom.innerText = text;

        var entity = arg.data;
    	if (entity && entity.getPrevious()){
    		var previousEntity = entity.getPrevious();
    		if (entity.get("tableName")==previousEntity.get("tableName")){
    			dom.innerText = "";
    		}
    	}
    }
});
self.set("#tableName.renderer", new MyDefaultCellRenderer1());

Button.ClientEvent:
var tableId="${request.getParameter('tableId')}";
var action = view.get("#saveMdFieldsAction");
var dialog = view.get("#updateMdFieldDialog");
if(tableId==""){
	action.execute(function(){
		dialog.hide();
	});
}else{
	dialog.hide();
}

