ToolBarButton.ClientEvent:
var dataSet = view.get("#dataSetMdTable");
var selection = view.get("#dataGridMdTable").get("selection");
var dataSetForVersion = view.get("#dataSetMdTableForVersion");
var dataSetFieldNew = view.get("#dataSetMdFieldNew");
var dataSetFieldOld = view.get("#dataSetMdFieldOld");

if(selection.length == 0){
	dorado.MessageBox.alert("请选择记录！");
}else if(selection.length > 2||selection.length == 1){
	dorado.MessageBox.alert("请选择二条记录比较！");
}else if(selection.length == 2){
	var tableIds = selection[0].get("id")+","+selection[1].get("id");
	paramTable = {tableIds:tableIds}
	if(selection[0].get("verNo") > selection[1].get("verNo")){
		var verNo1 = selection[1].get("verNo");
		var verNo2 = selection[0].get("verNo");
		dataSetForVersion.clear();
		dataSetForVersion.set("parameter",paramTable).flushAsync();
		var paramFieldNew = {tableName:selection[0].get("tableName"),verNo1:verNo1,verNo2:verNo2}
		dataSetFieldNew.clear();
		dataSetFieldNew.set("parameter",paramFieldNew).flushAsync();
		var paramFieldOld = {tableName:selection[0].get("tableName"),verNo1:verNo1,verNo2:verNo2}
		dataSetFieldOld.clear();
		dataSetFieldOld.set("parameter",paramFieldOld).flushAsync();
		view.get("#highVersionPanel").set("caption","版本号:  "+verNo2);
		view.get("#lowVersionPanel").set("caption","版本号:  "+verNo1);
	}else{
		var verNo1 = selection[0].get("verNo");
		var verNo2 = selection[1].get("verNo");
		dataSetForVersion.clear();
		dataSetForVersion.set("parameter",paramTable).flushAsync();
		var paramFieldNew = {tableName:selection[0].get("tableName"),verNo1:verNo1,verNo2:verNo2}
		dataSetFieldNew.clear();
		dataSetFieldNew.set("parameter",paramFieldNew).flushAsync();
		var paramFieldOld = {tableName:selection[0].get("tableName"),verNo1:verNo1,verNo2:verNo2}
		dataSetFieldOld.clear();
		dataSetFieldOld.set("parameter",paramFieldOld).flushAsync();
		view.get("#highVersionPanel").set("caption","版本号: "+verNo2);
		view.get("#lowVersionPanel").set("caption","版本号:  "+verNo1);
	}
	view.get("#versionManageDialog").show();
}

