var otherEntity = oldDs.getData('[@.get("fieldName")=="'+entity.get("fieldName")+'"]');

if(entity.get('updateFlg')=='1'){
	arg.processDefault=true; 
}else if(entity.get('updateFlg')=='2'){
	arg.dom.style.background = (entity.get('isParentid') != otherEntity.get('isParentid')) ? "#9F78FF" : "";
	arg.processDefault=true; 
}else{
	arg.processDefault=true; 
}

