var action = view.get("#ajaxActionTable");
var selection = view.get("#dataGridMdTable").get("selection");
var taskId;
if(selection.length==0){
	dorado.MessageBox.confirm("是否全部刷新？", function(){
		taskId = dorado.util.TaskIndicator.showTaskIndicator("【正在刷新请稍后...】","main");
		action.execute(function(result){
			view.get("#dataSetMdTable").flushAsync();
			dorado.util.TaskIndicator.hideTaskIndicator(taskId);
			if(result=='0'){
				action.set("successMessage","操作成功，元数据产生新的版本!");
			}else{
				action.set("successMessage","操作成功，元数据无变化！!");
			}
		});
	});
}else if (selection.length != 0) {
	dorado.MessageBox.confirm("是否刷新选中记录？", function(){
		var tableNames="";//定义一个id串。
		jQuery.each(selection,function(i,item){
			tableNames=tableNames+item.get("tableName")+",";
		});
		tableNames=tableNames.substring(0,tableNames.length-1);
		para={
			tableNames:tableNames
		}
		taskId = dorado.util.TaskIndicator.showTaskIndicator("【正在刷新请稍后...】","main");
		action.set("parameter",para).execute(function(result){
			view.get("#dataSetMdTable").flushAsync();
			dorado.util.TaskIndicator.hideTaskIndicator(taskId);
			if(result=='0'){
				action.set("successMessage","操作成功，元数据产生新的版本!");
			}else{
				action.set("successMessage","操作成功，元数据无变化！!");
			}
		});
	});
}

