Dialog.ClientEvent:
var fieldDataSet = view.getComponentReference("updateFieldIFrame").getIFrameWindow().$id("dataSetMdField").objects[0];
var fieldAction = view.getComponentReference("updateFieldIFrame").getIFrameWindow().$id("saveMdFieldsAction").objects[0];
var fieldEntityList = fieldDataSet.getData();
var num = 0;
	fieldEntityList.each(function(fieldEntity){

		if(fieldEntity.state == 2){
			num = num + 1;
		}
	});
if(num > 0){
	dorado.MessageBox.confirm("有"+num+"条未保存的表字段数据,是否保存？", {
		detailCallback: function(button, text) {
			if(button == "yes"){
				fieldAction.execute();
			view.get("#updateFieldDialog").hide();
			}
			if(button == "no"){
				view.get("#updateFieldDialog").hide();
			}
		}
	});
}else{
	view.get("#updateFieldDialog").hide();
}

