if(arg.selectedValue){
	var entity = new dorado.Entity();
	entity.fromJSON(JSON.parse(arg.selectedValue));
	var dataSet=view.get("#dataSetSysLogCfg");
	dataSet.getData("#").set("resourceCode",entity.get("menuCode"));
	dataSet.getData("#").set("resourceName",entity.get("menuName"));
	
	var menuUrl = entity.get("menuUrl");
	
	var serviceName='';
	view.get('#findServiceByMenuUrl').set('parameter',menuUrl).execute(function(result){
		serviceName=result;
	});
	if(serviceName!=''){
		var array = serviceName.split('|');
		dataSet.getData("#").set("objName",array[0]);
		dataSet.getData("#").set("objId",array[1]);	
	}
	dataSet.notifyObservers();
}
arg.selectedValue = undefined;
return false;

