Button.ClientEvent:
 var baseSelection = view.get("#bdataGridMdObjectMethod").get("selection");
 var selection = view.get("#dataGridMdObjectMethod").get("selection");
 
var dialog = view.get("#methodDialog");
var entitys = view.get("#dataSetSysLogCfg").getData();
var objId = view.get("#dataSetSysLogCfg").getData("#").get("objId");
var currentity = view.get("#dataSetSysLogCfg").getData("#");
var listGird = new Array();
jQuery.each(selection,function(i,item){
	listGird.push(item);
	});
	
jQuery.each(baseSelection,function(i,item){
	listGird.push(item);
	});

if(listGird.length==0){
	dorado.MessageBox.alert("请选择记录！");
}else if (listGird.length != 0) {
	jQuery.each(listGird,function(i,item){
		//忽略已经存在的按钮
		var bool = true;
		entitys.each(function(entity){
			if(item.get("methodName") == entity.get("methodName")&&item.get("objId") ==entity.get("objId")){
				bool = false;
				return true;
			}
		});
		if(bool){
			var entity = {
				objId:objId,
				objName:view.get("#dataSetSysLogCfg").getData("#").get('objName'),
				methodId:item.get("id"),
				methodName:item.get("methodName"),
				operType:item.get("methodOprType"),
				resourceName:view.get("#dataSetSysLogCfg").getData("#").get('resourceName'),
				resourceCode:view.get("#dataSetSysLogCfg").getData("#").get('resourceCode'),
				status:'1'
			};
			entitys.insert(entity);
			
		}
		
	});
	entitys.remove(currentity);
	dialog.hide();
	view.setFocus();
	view.get("#addDialogSysLogCfg").hide();
}

