DataType.ClientEvent:
if('isUserOrg'==arg.property){
	if(arg.entity.get('isUserOrg')=='1'){
		self.getPropertyDef('operOrgCodeName').set('readOnly',true);
		entity.get('operOrgCodeName','');
		entity.get('operOrgCode',null);
	}else{
		self.getPropertyDef('operOrgCodeName').set('readOnly',false);
	}
}else if('isUser'==arg.property){
	if(arg.entity.get('isUser')=='1'){
		self.getPropertyDef('operUserCodeName').set('readOnly',true);
		entity.get('operUserCodeName','');
		entity.get('operUserCode',null);
	}else{
		self.getPropertyDef('operUserCodeName').set('readOnly',false);
	}
}else if('nodeType'==arg.property){
	/* 暂不实现el表达式编辑逻辑，目前通过后台数据库进行配置，待有时间继续完善。
