ToolBarButton.ClientEvent:
var entitys = view.get("#dataSetWfProcessCfg").getData();
var selection = view.get("#dataGridWfProcessCfg").get("selection");
if(selection.length==0){
    dorado.MessageBox.alert("请选择记录！");
}else if(selection.length>1){
    dorado.MessageBox.alert("只能选择一条记录查看修改！");
}else if (selection.length == 1) {
    var entityClass = view.get('#dataSetWfProcessCfg').get("userData")[0].entityClass;
    var idValue = view.get("#dataSetWfProcessCfg").getData("#.id");
    var action=view.get("#checkPermissionAction");
    var idValueArray = new Array();
    idValueArray[0] = idValue;
    action.set("parameter",{
        entityClass:entityClass,
        idValues:idValueArray,
        operationType:'2'
    });
    action.execute(function(result){
        if(result.length>0){
            dorado.MessageBox.alert("不能进行此操作，您无选中记录的修改权限！");
            return;
        }
        view.get("#dataSetWfProcessCfg").getData().setCurrent(selection[0]);
        view.get("#dialogWfProcessCfg").show();
        view.get("#dataSetWfProcessCfg").set("readOnly",false);
    });
}

var entitys = view.get("#dataSetWfProcessCfg").getData();
                    var selection = view.get("#dataGridWfProcessCfg").get("selection");
                    var action = view.get("#saveWfProcessCfgsAction");
                    if(selection.length==0){
                        dorado.MessageBox.alert("请选择记录！");
                    }else if (selection.length != 0) {
                    var entityClass = view.get('#dataSetWfProcessCfg').get("userData")[0].entityClass;
                    var idValue = view.get("#dataSetWfProcessCfg").getData("#.id");
                    var checkPermissionAction=view.get("#checkPermissionAction");
                    var idValueArray = new Array();
                    jQuery.each(selection,function(i,item){
                    idValueArray[i] = item.get("id");
                    });
                    checkPermissionAction.set("parameter",{
                    entityClass:entityClass,
                    idValues:idValueArray,
                    operationType:'3'
                    });

                    checkPermissionAction.execute(function(result){
                    if(result.length>0){
                    var idValueFilter = new Object();
                    result.each(function(it){
                    idValueFilter[it] = true;
                    });
                    dorado.MessageBox.confirm("确定删除？", function(){
                    var haveRemove = false;
                    jQuery.each(selection,function(i,item){
                    if(!idValueFilter[item.get('id')]){
                    haveRemove = true;
                    entitys.remove(item);
                    }
                    });
                    if(haveRemove){
                    action.execute();
                    }
                    dorado.MessageBox.alert("您无选中记录的删除权限！");
                    return;
                    });
                    }else{
                    dorado.MessageBox.confirm("确定删除？", function(){
                    jQuery.each(selection,function(i,item){
                    entitys.remove(item);
                    });
                    action.execute();
                    });
                    }
                    });
                    }

var entitys = view.get("#dataSetWfProcessCfg").getData();
var found = false;
var selection = view.get("#dataGridWfProcessCfg").get("selection");
if(selection.length==0){
    dorado.MessageBox.alert("请选择记录！");
}else if(selection.length>1){
    dorado.MessageBox.alert("只能查看一条记录！");
}else if (selection.length == 1) {
    view.get("#dataSetWfProcessCfg").getData().setCurrent(selection[0]);
    view.get("#lookdialogWfProcessCfg").show();
    view.get("#dataSetWfProcessCfg").set("readOnly",true);
}

